<?php

namespace AppBundle\Repository;

/**
 * MacrocategorieMagRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MacrocategorieMagRepository extends \Doctrine\ORM\EntityRepository
{
    public function selectMacrocategorieMagParent($parentMultilangue, $locale)
    {
        return $this->createQueryBuilder('c')
            ->leftJoin('c.parentMultilangue', 'cp')
            ->where('cp.id = :id and c.locale = :locale')
            ->setParameter('id', $parentMultilangue)
            ->setParameter('locale', $locale)
            ->getQuery()
            ->getOneOrNullResult();
    }

    public function selectMacrocategorieMagParentMultilangue($id, $locale)
    {
        return $this->createQueryBuilder('c')
            ->select('COUNT(c.id)')
            ->leftJoin('c.parentMultilangue', 'cp')
            ->where('cp.id = :id and c.locale != :locale')
            ->setParameter('id', $id)
            ->setParameter('locale', $locale)
            ->getQuery()
            ->getSingleScalarResult();
    }

    public function selectMacrocategorieMagLocaleActive($localeActive)
    {
        return $this->createQueryBuilder('c')
            ->where('c.locale = :localeActive')
            ->setParameter('localeActive', $localeActive);
    }

    public function getTotalCategories($locale)
    {
        return $this->createQueryBuilder('c')
            ->select('COUNT(c) AS TOTAL')
            ->andWhere('c.locale = :locale')
            ->setParameter('locale', $locale)
            ->getQuery()
            ->getSingleScalarResult();
    }
}
